/** @format */

import axios from "axios";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Logo from "../assets/logo.svg";

import GroupChatmodal from "./GroupChatmodal";

export default function Contacts({
	contacts,
	currentUser,
	changeChat,
	groupChatName,
}) {
	const [currentUserName, setCurrentUserName] = useState(undefined);
	const [currentUserImage, setCurrentUserImage] = useState(undefined);
	const [currentSelected, setCurrentSelected] = useState(undefined);
	const [groupSelected, setGroupSelected] = useState(undefined);
	const [activeComponent, setActiveComponent] = useState(undefined);

	useEffect(() => {
		if (currentUser) {
			setCurrentUserImage(currentUser.avatarImage);
			setCurrentUserName(currentUser.username);
		}
	}, [currentUser]);

	const changeCurrentChat = (index, contact) => {
		setCurrentSelected(index);
		changeChat(contact);
	};
	const changeGroupChat = (index, groupChatName) => {
		setGroupSelected(index);
		changeChat(groupChatName);
	};

	const handlechats = (changeCurrentChat) => {
		changeCurrentChat ? setActiveComponent(false) : setActiveComponent(true);
	};
	console.log("active compoenet : " + activeComponent);

	return (
		<>
			{currentUserImage && currentUserName && (
				<Container>
					<div className="brand">
						<img src={Logo} alt="logo" />
						<h3>Chat-App</h3>
						<GroupChatmodal>
							<button>new group chat +</button>
						</GroupChatmodal>
					</div>
					<div
						className="contacts"
						onClick={() => {
							handlechats();
						}}>
						{groupChatName?.map((groupChatName, index) => {
							return (
								<div
									style={{ color: "white" }}
									key={groupChatName._id}
									onClick={() => changeGroupChat(index, groupChatName)}>
									<div className="avatar">
										<img
											src={
												"data:image/jpeg;base64,"
											}
											alt="avatar"
										/>
									</div>
									<div className="username">
										<h3>Group : {groupChatName.name}</h3>
									</div>
								</div>
							);
						})}
						{contacts.map((contact, index) => {
							return (
								<div
									className={`contact ${
										index === currentSelected ? "selected" : ""
									}`}
									key={contact._id}
									onClick={() => changeCurrentChat(index, contact)}>
									<div className="avatar">
										<img
											src={`data:image/svg+xml;base64,${contact.avatarImage}`}
											alt="avatar"
										/>
									</div>
									<div className="username">
										<h3>{contact.username}</h3>
									</div>
								</div>
							);
						})}
					</div>

					<div className="current-user">
						<div className="avatar">
							<img
								src={`data:image/svg+xml;base64,${currentUserImage}`}
								alt="avatar"
							/>
						</div>
						<div className="username">
							<h2>{currentUserName}</h2>
						</div>
					</div>
				</Container>
			)}
		</>
	);
}

const Container = styled.div`
	display: grid;
	grid-template-rows: 10% 75% 15%;
	overflow: hidden;
	background-color: #080420;
	.brand {
		display: flex;
		align-items: center;
		gap: 1rem;
		justify-content: center;
		img {
			height: 2rem;
		}
		h3 {
			color: white;
			text-transform: uppercase;
		}
	}
	.contacts {
		display: flex;
		flex-direction: column;
		align-items: center;
		overflow: auto;
		gap: 0.8rem;
		&::-webkit-scrollbar {
			width: 0.2rem;
			&-thumb {
				background-color: #ffffff39;
				width: 0.1rem;
				border-radius: 1rem;
			}
		}
		.contact {
			background-color: #ffffff34;
			min-height: 5rem;
			cursor: pointer;
			width: 90%;
			border-radius: 0.2rem;
			padding: 0.4rem;
			display: flex;
			gap: 1rem;
			align-items: center;
			transition: 0.5s ease-in-out;
			.avatar {
				img {
					height: 3rem;
				}
			}
			.username {
				h3 {
					color: white;
					text-transform: capitalize;
				}
			}
		}
		.selected {
			background-color: #9a86f3;
		}
	}
	.current-user {
		background-color: #0d0d30;
		display: flex;
		justify-content: center;
		align-items: center;
		gap: 2rem;
		.avatar {
			img {
				height: 4rem;
				max-inline-size: 100%;
			}
		}
		.username {
			h2 {
				color: white;
				text-transform: capitalize;
			}
		}
		@media screen and (min-width: 720px) and (max-width: 1080px) {
			gap: 0.5rem;
			.username {
				h2 {
					font-size: 1rem;
				}
			}
		}
	}
`;
